import React, { useState, useRef } from "react";
import Draggable from "react-draggable";
import { ResizableBox } from "react-resizable";
import "react-resizable/css/styles.css";

type ShapeType = "square" | "circle" | "triangle";

type DraggableCardProps = {
  shape: ShapeType;
  onRemove: () => void;
  initialPosition: { x: number; y: number };
};

const DraggableCard: React.FC<DraggableCardProps> = ({ shape, onRemove,initialPosition }) => {
  const [cardText, setCardText] = useState<string>("text");
  const [disableDragging, setDisableDragging] = useState<boolean>(false);

  const [currentShape, setCurrentShape] = useState<ShapeType | null>(null);
  const shapeRef = useRef<HTMLDivElement | null>(null);

  const shapeStyles = {
    square: {},
    circle: {
      borderRadius: "50%",
    },
    triangle: {
      clipPath: "polygon(50% 0%, 0% 100%, 100% 100%)",
    },
  };

  const handleTextChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setCardText(event.target.value);
  };

  const handleStart = () => {
    setDisableDragging(true);
  };

  const handleStop = () => {
    setDisableDragging(false);
  };

  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Delete") {
      onRemove();
    }
  };


  return (
    <div className="draggable-container">
      {currentShape && (
        <Draggable
          disabled={disableDragging}
          defaultPosition={initialPosition}
          onStart={handleStart}
          onStop={handleStop}
        >
          <div
            ref={shapeRef}
            onKeyDown={handleKeyDown}
            tabIndex={0}
            style={{
              position: "absolute",
              top: "0",
              left: "0",
            }}
          >
            <ResizableBox
              resizeHandles={["nw", "ne", "sw", "se", "n", "e", "s", "w"]}
              width={200}
              height={150}
              style={{
                ...shapeStyles[currentShape],
                display: "flex",
                justifyContent: "center",
                alignItems: "center",
                backgroundColor: "lightblue",
                border: "1px solid blue",
              }}
            >
              <textarea
                value={cardText}
                onChange={handleTextChange}
                style={{
                  ...shapeStyles[currentShape],
                  backgroundColor: "lightblue",
                  resize: "none",
                  border: "none",
                  outline: "none",
                  flex: 1,
                  textAlign: "center",
                  display: "flex",
                  justifyContent: "center",
                  alignItems: "center",
                }}
              />
            </ResizableBox>
          </div>
        </Draggable>
      )}
      
    </div>
  );
};

export default DraggableCard;
