import { Modal, Button } from "react-bootstrap";
import React from "react";

type ShapeType = "square" | "circle" | "triangle";

const shapesList: { type: ShapeType; label: string }[] = [
  { type: "square", label: "Square" },
  { type: "circle", label: "Circle" },
  { type: "triangle", label: "Triangle" },
];

type ShapeModalProps = {
  isOpen: boolean;
  onClose: () => void;
  onShapeSelect: (shape: ShapeType) => void;
};

const ShapeModal = ({ isOpen, onClose, onShapeSelect }: ShapeModalProps) => {
    const handleDragStart = (shape: ShapeType) => (event: React.DragEvent) => {
        event.dataTransfer.setData("shape", shape);
      };
  return (
    <Modal show={isOpen} onHide={onClose}>
      <Modal.Header>
        <Modal.Title>Choose a Shape</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <div className="list-shape">
          {shapesList.map((shape) => (
            <div
              key={shape.type}
              draggable
              onDragStart={handleDragStart(shape.type)}
              className={`shape ${shape.type}`}
            >
              {shape.label}
            </div>
          ))}
        </div>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onClose}>
          Close
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default React.memo(ShapeModal);
