import React, { useState } from "react";
import Draggable from "react-draggable";
import { ResizableBox } from "react-resizable";
import "react-resizable/css/styles.css";
type DraggableCardProps = {
  shape: "square" | "circle" | "triangle";
  onRemove: () => void;
};
const DraggableCard: React.FC<DraggableCardProps> = ({ shape, onRemove }) => {
  const [cardText, setCardText] = useState<string>("text");
  const [disableDragging, setDisableDragging] = useState<boolean>(false);

  const shapeStyles = {
    square: {},
    circle: {
      borderRadius: "50%",
    },
    triangle: {
      clipPath: "polygon(50% 0%, 0% 100%, 100% 100%)",
    },
  };
  const handleTextChange = (event: React.ChangeEvent<HTMLTextAreaElement>) => {
    setCardText(event.target.value);
  };

  const handleStart = () => {
    setDisableDragging(true);
  };

  const handleStop = () => {
    setDisableDragging(false);
  };
  const handleKeyDown = (event: React.KeyboardEvent) => {
    if (event.key === "Delete") {
      onRemove();
    }
  };

  const handleDragStop = (e: any, data: any) => {
    // Khi kéo shape vào iframe, tạo bản sao của shape và gọi hàm onRemove để ẩn shape gốc
    if (data.x + data.node.offsetWidth > window.innerWidth * 0.7) {
      // Tạo bản sao của shape tại vị trí hiện tại
      // (Bạn có thể thêm mã ở đây để tạo bản sao của shape trong iframe)
      
      onRemove(); // Ẩn shape gốc
    }
  };



  return (
    <div className=" draggable-container" style={{ position: 'fixed', right: '10px', top: '10px' }}>
      <Draggable
        disabled={disableDragging}
        onStart={handleStart}
        onStop={handleDragStop}
      >

<div onKeyDown={handleKeyDown} tabIndex={0}>
<ResizableBox

resizeHandles={["nw", "ne", "sw", "se", "n", "e", "s", "w"]}
width={200}
height={150}
style={{
  ...shapeStyles[shape],
  display: "flex",
  justifyContent: "center",
  alignItems: "center",
  backgroundColor: "lightblue",
  border: "1px solid blue",
}}
>
<textarea

  value={cardText}
  onChange={handleTextChange}
  style={{
    ...shapeStyles[shape],
    backgroundColor: "lightblue",
    resize: "none",
    border: "none",
    outline: "none",
    flex: 1,
    textAlign: "center",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
  }}
/>
</ResizableBox>
</div>
      </Draggable>
    </div>
  );
};

export default DraggableCard;
