import React, { useState, useEffect, useRef } from "react";
import { useParams } from "react-router-dom";
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import DraggableCard from "./DraggableCard";
import { DocRenderer } from "@cyntler/react-doc-viewer";
import documents from "../documents";


type ShapeType = "square" | "circle" | "triangle";
type State = {
  fileType: "pdf" | "xlsx" | "docx" | "image" | "ppt";
  viewerHeight: string;
  shapes: Array<"square" | "circle" | "triangle">;
  selectedShapeIndex: number | null;
};
type DocumentType = {
  id: string;
  name: string;
  uri: string;
  fileType: "pdf" | "xlsx" | "docx" | "image" | "ppt";
};

type DocViewerProps = {
  documents: Array<{ uri: string }>;
  pluginRenderers: DocRenderer[];
  config: {
    header: {
      disableHeader: boolean;
      disableFileName: boolean;
      retainURLParams: boolean;
    };
  };
  style: { height: string };
};

const ControlledDocViewer = React.memo<DocViewerProps>(
  (props) => {
    return <DocViewer {...props} />;
  },
  (prevProps, nextProps) => {
    return prevProps.documents[0].uri === nextProps.documents[0].uri;
  }
);

const DocumentViewer: React.FC = () => {
  
  const [fileType, setFileType] = useState<"pdf" | "xlsx" | "docx" | "image" | "ppt">("pdf");
const {id}=useParams<{id:string}>();
const [document, setDocument] = useState<DocumentType | null>(null);
  const [viewerHeight, setViewerHeight] = useState<string>("100%");
  const [shapes, setShapes] = useState<ShapeType[]>([]);
  // const [selectedShapeIndex, setSelectedShapeIndex] = useState<number | null>(
  //   null
  // );
  const docViewerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const foundDocument = documents.find((doc) => doc.id === id) as DocumentType | undefined;
    if (foundDocument) {
      setDocument(foundDocument);
      setFileType(foundDocument.fileType);
    } else {
      setDocument(null);
    }
  }, [id]);

  useEffect(() => {
    const updateViewerHeight = () => {
      if (docViewerRef.current) {
        const documentHeight = docViewerRef.current.scrollHeight;
        switch (document?.fileType) {
          case "pdf":
            setViewerHeight(`${documentHeight}px`);
            break;
          case "xlsx":
          case "docx":
          case "ppt":
            setViewerHeight("500vh");
            break;
          default:
            break;
        }
      }
    };
    updateViewerHeight();
    window.addEventListener("resize", updateViewerHeight);

    return () => {
      window.removeEventListener("resize", updateViewerHeight);
    };
  }, [document]);

  if (!document) {
    return <div>Không tìm thấy tài liệu</div>;
  }
  const getDocuments = () => {
    return [{ uri: document.uri }];
  };

  const toggleFileType = () => {
    const fileTypes = ["pdf", "xlsx", "docx", "ppt"];
    const currentIndex = fileTypes.indexOf(fileType);
    const nextIndex = (currentIndex + 1) % fileTypes.length;
    setFileType(
      fileTypes[nextIndex] as "pdf" | "xlsx" | "docx" | "image" | "ppt"
    );
    setShapes([]); // Reset shapes
  };



  const addShape = (shape: ShapeType) => {
    setShapes((prevShapes) => [...prevShapes, shape]);
  };

  const removeShape = (indexToRemove: number) => {
    const newShapes = shapes.filter((_, index) => index !== indexToRemove);
    setShapes(newShapes);
    // setSelectedShapeIndex(null);
  };

  // const selectShape = (index: number) => {
  //   setSelectedShapeIndex(index);
  // };

  // const handleShapeSelect = (shape: "square" | "circle" | "triangle") => {
  //   addShape(shape);
  // };
  return (
    <>
    <div className={`viewer-with-draggable ${document.fileType}`}>
        <div className="right-panel">
          {document.fileType === "pdf" && (
      <iframe
        src={`https://docs.google.com/viewer?url=${encodeURIComponent(document.uri)}&embedded=true`}
        width="100%"
        height="100%"
        frameBorder="0"
      />
    )}
    {document.fileType === "xlsx" && (
      <iframe
        src={`https://docs.google.com/viewer?url=${encodeURIComponent(document.uri)}&embedded=true`}
        width="100%"
        height="100%"
        frameBorder="0"
      />
    )}
    {document.fileType === "docx" && (
      <iframe
        src={`https://docs.google.com/viewer?url=${encodeURIComponent(document.uri)}&embedded=true`}
        width="100%"
        height="100%"
        frameBorder="0"
      />
    )}
    {document.fileType === "ppt" && (
      <iframe
        src={`https://docs.google.com/viewer?url=${encodeURIComponent(document.uri)}&embedded=true`}
        width="100%"
        height="100%"
        frameBorder="0"
      />
    )}
        </div>
        <div className="left-panel">
          <div className="shapes-container">
            {shapes.map((shape, index) => (
              <DraggableCard
                key={index}
                shape={shape}
                onRemove={() => removeShape(index)}
              />
            ))}
          </div>
          <div className="button-shape-container">

              
            <button onClick={() => addShape("square")}>square</button>
            <button onClick={() => addShape("circle")}>circle</button>
            <button onClick={() => addShape("triangle")}>triangle</button>
            </div>
       


          <div className="button-shape-container-content">
            <div className="button-shape-container-content-text">
              <textarea placeholder="Nhập thông tin ở đây"></textarea>
            </div>
            <div className="button-shape-container-content-state">
              アサイン
              <select>
                <option value="option1">HOANG VAN HIEU</option>
                <option value="option2">NGUYEN VAN DUNG</option>
                <option value="option3">HUYNH TUAN THANH</option>
                <option value="option1">NGUYEN VAN VAN</option>
                <option value="option2">VO NHAT QUANG</option>
              </select>
              ステータス
              <select>
                <option value="option1">未着手</option>
                <option value="option2">進行中</option>
                <option value="option3">完了</option>
              </select>
            </div>
          </div>
        </div>
      </div>

      <footer>
        <button className="button-link" onClick={toggleFileType}>
          Chuyển đổi
        </button>
      </footer>
    </>
  );
};

export default DocumentViewer;
