import { createContext, useState, useContext } from "react";
type DocumentsContextType = {
  documents: DocumentType[];
  updateDocuments: (updatedDocuments: DocumentType[]) => void;
};

const DocumentsContext = createContext<DocumentsContextType>({
  documents: [],
  updateDocuments: () => {},
});

export const DocumentsProvider: React.FC = ({
  children,
}: React.PropsWithChildren<{}>) => {
  const [documents, setDocuments] = useState<DocumentType[]>([]);

  const updateDocuments = (updatedDocuments: DocumentType[]) => {
    setDocuments(updatedDocuments);
    localStorage.setItem("documents", JSON.stringify(updatedDocuments));
  };
  return (
    <DocumentsContext.Provider value={{ documents, updateDocuments }}>
      {children}
    </DocumentsContext.Provider>
  );
};

export const useDocuments = () => {
  return useContext(DocumentsContext);
};

export {}
