import React, { useState, useEffect, useRef } from "react";
import Draggable from "react-draggable";
import DocViewer, { DocViewerRenderers } from "@cyntler/react-doc-viewer";
import DraggableCard from "./DraggableCard";
import { DocRenderer } from "@cyntler/react-doc-viewer";
import ShapeModal from "./ShapeModal";


type State = {
  fileType: "pdf" | "xlsx" | "docx" | "image"|"ppt";
  viewerHeight: string;
  shapes: Array<"square" | "circle" | "triangle">;
  selectedShapeIndex: number | null;
};
type DocViewerProps = {
  documents: Array<{ uri: string }>;
  pluginRenderers: DocRenderer[];
  config: {
    header: {
      disableHeader: boolean;
      disableFileName: boolean;
      retainURLParams: boolean;
    };
  };
  style: { height: string };
};


const ControlledDocViewer: React.FC<DocViewerProps> = (props) => {
  const shouldComponentUpdate = (nextProps: DocViewerProps) => {
    return nextProps.documents[0].uri !== props.documents[0].uri;
  };

  return shouldComponentUpdate(props) ? <DocViewer {...props} /> : null;
};


const DocumentViewer: React.FC = () => {
  const [fileType, setFileType] = useState<"pdf" | "xlsx" | "docx" | "image" | "ppt">("pdf");
  const [viewerHeight, setViewerHeight] = useState<string>("100%");
  const [shapes, setShapes] = useState<Array<"square" | "circle" | "triangle">>([]);
  const [selectedShapeIndex, setSelectedShapeIndex] = useState<number | null>(null);
  const docViewerRef = useRef<HTMLDivElement>(null);
  const [isModalOpen, setModalOpen] = useState<boolean>(false);
  useEffect(() => {
    const updateViewerHeight = () => {
      if (docViewerRef.current) {
        const documentHeight = docViewerRef.current.scrollHeight;
        switch (fileType) {
          case "pdf":
            setViewerHeight(`${documentHeight}px`);
            break;
          case "xlsx":
            setViewerHeight(`300vh`);
            break;
          case "docx":
          case "ppt":
            setViewerHeight("300vh");
            break;
          default:
            break;
        }
      }
    };
    updateViewerHeight();
    window.addEventListener("resize", updateViewerHeight);

    return () => {
      window.removeEventListener("resize", updateViewerHeight);
    };
  }, [fileType]);

  const toggleFileType = () => {
    const fileTypes = ["pdf", "xlsx", "docx", "ppt"];
    const currentIndex = fileTypes.indexOf(fileType);
    const nextIndex = (currentIndex + 1) % fileTypes.length;
    setFileType(fileTypes[nextIndex] as "pdf" | "xlsx" | "docx" | "image" | "ppt");
    setShapes([]); // Reset shapes
  };

  const getDocuments = () => {
    switch (fileType) {
      case "pdf":
        return [{ uri: process.env.PUBLIC_URL + "/c4611_sample_explain.pdf" }];
      case "xlsx":
        return [
          {
            uri: "https://transfer.sh/c1onBs00w2/%E5%8F%82%E6%95%B0%E8%A1%A8.xlsx",
          },
        ];
      case "docx":
        return [{ uri: "https://transfer.sh/rvq45hMe0N/sample1.docx" }];
      case "ppt":
        return [{ uri: "https://transfer.sh/9hXYSLu39h/file_example_PPT_1MB.ppt" }];
      default:
        return [];
    }
  };



 


  const addShape = (shape: "square" | "circle" | "triangle") => {
    setShapes((prevShapes) => [...prevShapes, shape]);
  };

  const removeShape = (indexToRemove: number) => {
    const newShapes = shapes.filter((_, index) => index !== indexToRemove);
    setShapes(newShapes);
    setSelectedShapeIndex(null);
  };

  const selectShape = (index: number) => {
    setSelectedShapeIndex(index);
  };

  const openShapeModal = () => setModalOpen(true);
  const closeShapeModal = () => setModalOpen(false);

  const handleShapeSelect = (shape: "square" | "circle" | "triangle") => {
    addShape(shape);
    closeShapeModal();
  };
    return (
      <>
       <div className={`pdf-viewer-with-draggable ${fileType === "xlsx" ? "xlsx" : ""}`}>
          <div className="right-panel">
          {fileType === "pdf" ? (
              <ControlledDocViewer
            
                pluginRenderers={DocViewerRenderers}
                documents={getDocuments()}
                config={{
                  header: {
                    disableHeader: false,
                    disableFileName: false,
                    retainURLParams: false,
                  },
                }}
                style={{ height: viewerHeight }}
              />
              ) : fileType === "xlsx" ? (
              <iframe
                src={`https://docs.google.com/viewer?url=${encodeURIComponent(
                  "https://transfer.sh/c1onBs00w2/%E5%8F%82%E6%95%B0%E8%A1%A8.xlsx"
                )}&embedded=true`}
                width="100%"
                height="100%"
                frameBorder="0"
              />
              ) : fileType === "docx" ? (
              <iframe
                src={`https://docs.google.com/viewer?url=${encodeURIComponent(
                  "https://transfer.sh/rvq45hMe0N/sample1.docx"
                )}&embedded=true`}
                width="100%"
                height="100%"
                frameBorder="0"
              />
              ) : fileType === "ppt" ? (
              <iframe
              src={`https://docs.google.com/viewer?url=${encodeURIComponent(
                "https://transfer.sh/9hXYSLu39h/file_example_PPT_1MB.ppt"
              )}&embedded=true`}
              width="100%"
              height="100%"
              frameBorder="0"
            />
            ) : null}
          </div>
          <div className="left-panel">
          <div className="shapes-container">
            {shapes.map((shape, index) => (
              <DraggableCard key={index} shape={shape} onRemove={() => removeShape(index)} />
            ))}
          </div>
            <div className="button-shape-container">
            <button onClick={() => addShape("square")}>square</button>
            <button onClick={() => addShape("circle")}>circle</button>
            <button onClick={() => addShape("triangle")}>triangle</button>
            </div>
            <div className="button-shape-container-content">
              <div className="button-shape-container-content-text">
                <textarea placeholder="Nhập thông tin ở đây"></textarea>
              </div>
              <div className="button-shape-container-content-state">
                アサイン
                <select>
                  <option value="option1">HOANG VAN HIEU</option>
                  <option value="option2">NGUYEN VAN DUNG</option>
                  <option value="option3">HUYNH TUAN THANH</option>
                  <option value="option1">NGUYEN VAN VAN</option>
                  <option value="option2">VO NHAT QUANG</option>
                </select>
                ステータス
                <select>
                  <option value="option1">未着手</option>
                  <option value="option2">進行中</option>
                  <option value="option3">完了</option>
                </select>
              </div>
            </div>
          </div>
        </div>
        <footer>
        <button className="button-link" onClick={toggleFileType}>
            Chuyển đổi
          </button>
        </footer>
        <button onClick={openShapeModal}>Add Shape</button>
      <ShapeModal isOpen={isModalOpen} onClose={closeShapeModal} onShapeSelect={handleShapeSelect} />
      </>
    );
  }

export default DocumentViewer;
